import React, { useState } from "react";
import "./index.css";

const positions = ["Kaleci", "Defans", "Orta Saha", "Forvet"];

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teams, setTeams] = useState([]);
  const [matchResult, setMatchResult] = useState(null);
  const [teamNames, setTeamNames] = useState(["Takım 1", "Takım 2"]);

  const parsePlayers = () => {
    return names
      .split("\n")
      .map((line) => {
        const [name, skillStr, pos] = line.split(",").map((x) => x.trim());
        return {
          name,
          skill: skillStr ? Number(skillStr) : Math.floor(Math.random() * 10) + 1,
          position: positions.includes(pos) ? pos : positions[Math.floor(Math.random() * positions.length)],
        };
      })
      .filter((player) => player.name !== "");
  };

  const handleGenerate = () => {
    const allPlayers = parsePlayers();
    const result = [[], []];
    allPlayers.forEach((player, index) => {
      result[index % 2].push(player);
    });
    setTeams(result);
    setMatchResult(null);
  };

  const simulateMatch = () => {
    if (teams.length !== 2) {
      alert("Simülasyon için tam 2 takım olmalı.");
      return;
    }

    const team1Skill = teams[0].reduce((sum, player) => sum + player.skill, 0);
    const team2Skill = teams[1].reduce((sum, player) => sum + player.skill, 0);

    const goalsTeam1 = Math.floor(Math.random() * (team1Skill / 5 + 1));
    const goalsTeam2 = Math.floor(Math.random() * (team2Skill / 5 + 1));

    const generateGoals = (teamIndex, goalsCount) => {
      const teamPlayers = teams[teamIndex];
      const goals = [];
      for (let i = 0; i < goalsCount; i++) {
        const minute = Math.floor(Math.random() * 90) + 1;
        const scorer = teamPlayers[Math.floor(Math.random() * teamPlayers.length)];
        goals.push({ minute, scorer });
      }
      goals.sort((a, b) => a.minute - b.minute);
      return goals;
    };

    const goalsTeam1Arr = generateGoals(0, goalsTeam1);
    const goalsTeam2Arr = generateGoals(1, goalsTeam2);

    setMatchResult({
      goalsTeam1: goalsTeam1Arr,
      goalsTeam2: goalsTeam2Arr,
      score: [goalsTeam1, goalsTeam2],
    });
  };

  const resetAll = () => {
    setNames("");
    setTeams([]);
    setMatchResult(null);
    setTeamNames(["Takım 1", "Takım 2"]);
  };

  return (
    <div className="App">
      <h1>Rastgele Takım Oluşturucu ve Maç Simülasyonu</h1>

      <textarea
        placeholder="Her satıra 'İsim,Seviye(1-10),Pozisyon' yazın"
        value={names}
        onChange={(e) => setNames(e.target.value)}
        rows={6}
      />

      <div style={{ display: "flex", gap: "1rem", marginBottom: "1rem", flexWrap: "wrap", justifyContent: "center" }}>
        <input type="text" value={teamNames[0]} onChange={(e) => setTeamNames([e.target.value, teamNames[1]])} />
        <input type="text" value={teamNames[1]} onChange={(e) => setTeamNames([teamNames[0], e.target.value])} />
      </div>

      <div>
        <button className="generate" onClick={handleGenerate}>Takımları Oluştur</button>
        <button className="simulate" onClick={simulateMatch} disabled={teams.length !== 2}>Maçı Simüle Et</button>
        <button className="reset" onClick={resetAll}>Sıfırla</button>
      </div>

      <div className="team-container">
        {teams.map((team, index) => (
          <div key={index} className="team-box">
            <h2 className="team-title">{teamNames[index]}</h2>
            {positions.map((pos) => (
              <div key={pos} className="position-row">
                {team.filter((p) => p.position === pos).map((player, i) => (
                  <div key={i} className="player-circle" title={`${player.name} (Skill: ${player.skill})`}>
                    <div>{player.name.split(" ")[0]}</div>
                    <div>({player.skill})</div>
                  </div>
                ))}
              </div>
            ))}
          </div>
        ))}
      </div>

      {matchResult && (
        <div className="match-result">
          <h2>Maç Sonucu: {teamNames[0]} {matchResult.score[0]} - {matchResult.score[1]} {teamNames[1]}</h2>
          <div className="goal-list">
            <div>
              <h3>{teamNames[0]} Goller</h3>
              {matchResult.goalsTeam1.length === 0 ? (
                <p>Gol Yok</p>
              ) : (
                <ul>
                  {matchResult.goalsTeam1.map(({ minute, scorer }, i) => (
                    <li key={i}>⚽ {minute}' - {scorer.name}</li>
                  ))}
                </ul>
              )}
            </div>
            <div>
              <h3>{teamNames[1]} Goller</h3>
              {matchResult.goalsTeam2.length === 0 ? (
                <p>Gol Yok</p>
              ) : (
                <ul>
                  {matchResult.goalsTeam2.map(({ minute, scorer }, i) => (
                    <li key={i}>⚽ {minute}' - {scorer.name}</li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default RandomTeamMaker;
