import React, { useState } from "react";

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teamCount, setTeamCount] = useState(2);
  const [teams, setTeams] = useState([]);

  const handleGenerate = () => {
    const nameArray = names
      .split("\n")
      .map((name) => name.trim())
      .filter((name) => name !== "");
    const shuffled = [...nameArray].sort(() => 0.5 - Math.random());
    const result = Array.from({ length: teamCount }, () => []);
    shuffled.forEach((name, index) => {
      result[index % teamCount].push(name);
    });
    setTeams(result);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#2e7d32",
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      {/* Ana içerik */}
      <main className="p-6 max-w-5xl mx-auto flex-grow">
        <h1 className="text-3xl font-bold mb-6 text-center">
          ⚽ Rastgele Futbol Takımı Oluşturucu
        </h1>

        {/* Textarea */}
        <textarea
          className="w-full mb-4"
          rows={6}
          placeholder="Her satıra bir futbolcu ismi yazın"
          value={names}
          onChange={(e) => setNames(e.target.value)}
          style={{
            padding: "12px",
            borderRadius: "10px",
            border: "3px solid #4caf50",
            fontSize: "1rem",
            resize: "vertical",
            fontWeight: "600",
            outlineColor: "#81c784",
          }}
        />

        {/* Takım sayısı input */}
        <div className="mb-4 flex items-center gap-3">
          <label>Takım Sayısı:</label>
          <input
            type="number"
            min="1"
            className="rounded px-3 py-1 text-black font-semibold"
            style={{ width: "60px" }}
            value={teamCount}
            onChange={(e) => setTeamCount(Number(e.target.value))}
          />
        </div>

        {/* Buton */}
        <button
          onClick={handleGenerate}
          style={{
            backgroundColor: "transparent",
            color: "#a5d6a7",
            border: "3px solid #4caf50",
            padding: "10px 30px",
            borderRadius: "15px",
            fontWeight: "700",
            fontSize: "1.1rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            boxShadow: "0 0 5px #4caf50",
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = "#4caf50";
            e.currentTarget.style.color = "white";
            e.currentTarget.style.boxShadow = "0 0 12px #388e3c";
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = "transparent";
            e.currentTarget.style.color = "#a5d6a7";
            e.currentTarget.style.boxShadow = "0 0 5px #4caf50";
          }}
        >
          Takımları Oluştur
        </button>

        {/* Takım sahaları */}
        <div className="mt-10 grid grid-cols-1 md:grid-cols-2 gap-6">
          {teams.map((team, index) => {
            const half = Math.ceil(team.length / 2);
            const topRow = team.slice(0, half);
            const bottomRow = team.slice(half);

            return (
              <div
                key={index}
                style={{
                  backgroundColor: "#1b5e20",
                  border: "4px dashed #cddc39",
                  borderRadius: "20px",
                  padding: "20px",
                  position: "relative",
                  minHeight: "250px",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                  justifyContent: "center",
                }}
              >
                {/* Kale */}
                <div
                  style={{
                    position: "absolute",
                    top: "10px",
                    left: "calc(50% - 30px)",
                    fontSize: "1.5rem",
                  }}
                >
                  🧤
                </div>

                <h2 className="text-xl font-bold mb-4 text-center">
                  Takım {index + 1}
                </h2>

                {/* Üst sıra */}
                <div className="flex justify-center gap-3 mb-4 flex-wrap">
                  {topRow.map((name, i) => (
                    <div
                      key={i}
                      className="bg-green-300 text-black px-3 py-1 rounded-full shadow"
                      style={{
                        fontWeight: "600",
                        minWidth: "80px",
                        textAlign: "center",
                      }}
                    >
                      {name}
                    </div>
                  ))}
                </div>

                {/* Orta daire */}
                <div
                  style={{
                    width: "60px",
                    height: "60px",
                    border: "2px solid white",
                    borderRadius: "50%",
                    margin: "0 auto 10px",
                  }}
                ></div>

                {/* Alt sıra */}
                <div className="flex justify-center gap-3 flex-wrap">
                  {bottomRow.map((name, i) => (
                    <div
                      key={i}
                      className="bg-green-300 text-black px-3 py-1 rounded-full shadow"
                      style={{
                        fontWeight: "600",
                        minWidth: "80px",
                        textAlign: "center",
                      }}
                    >
                      {name}
                    </div>
                  ))}
                </div>

                {/* Alt kale */}
                <div
                  style={{
                    position: "absolute",
                    bottom: "10px",
                    left: "calc(50% - 30px)",
                    fontSize: "1.5rem",
                  }}
                >
                  🧤
                </div>
              </div>
            );
          })}
        </div>
      </main>

      {/* Footer */}
      <footer
        style={{
          backgroundColor: "#1b5e20",
          padding: "1rem 0",
          textAlign: "center",
          borderTop: "2px solid #cddc39",
        }}
      >
        <div style={{ fontSize: "1.5rem", marginBottom: "0.5rem" }}>
          <a
            href="https://twitter.com"
            target="_blank"
            rel="noopener noreferrer"
            style={{ margin: "0 15px", color: "white", textDecoration: "none" }}
            aria-label="Twitter"
          >
            🐦
          </a>
          <a
            href="https://instagram.com"
            target="_blank"
            rel="noopener noreferrer"
            style={{ margin: "0 15px", color: "white", textDecoration: "none" }}
            aria-label="Instagram"
          >
            📸
          </a>
          <a
            href="https://facebook.com"
            target="_blank"
            rel="noopener noreferrer"
            style={{ margin: "0 15px", color: "white", textDecoration: "none" }}
            aria-label="Facebook"
          >
            📘
          </a>
        </div>
        <div style={{ fontSize: "0.9rem", opacity: 0.7 }}>
          © 2025 Futbol Takım Oluşturucu
        </div>
      </footer>
    </div>
  );
}

export default RandomTeamMaker;
