import React, { useState } from "react";

const positions = ["Kaleci", "Defans", "Orta Saha", "Forvet"];

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teams, setTeams] = useState([]);
  const [matchResult, setMatchResult] = useState(null);

  // Oyuncuları pozisyon ve skill'e göre oluşturup takımlara dengeli dağıtma
  const handleGenerate = () => {
    const nameArray = names
      .split("\n")
      .map((line) => {
        const [name, skillStr, pos] = line.split(",").map((x) => x.trim());
        return {
          name,
          skill: skillStr ? Number(skillStr) : Math.floor(Math.random() * 10) + 1,
          position: positions.includes(pos) ? pos : positions[Math.floor(Math.random() * positions.length)],
        };
      })
      .filter((player) => player.name !== "");

    // Pozisyonlara göre gruplandır
    const groupedByPosition = {};
    positions.forEach((pos) => (groupedByPosition[pos] = []));
    nameArray.forEach((player) => groupedByPosition[player.position].push(player));

    // İki takım için oyuncuları pozisyona göre dönüşümlü dağıt
    const result = [[], []];
    positions.forEach((pos) => {
      groupedByPosition[pos].forEach((player, i) => {
        result[i % 2].push(player);
      });
    });

    setTeams(result);
    setMatchResult(null);
  };

  // Maç simülasyonu fonksiyonu
  const simulateMatch = () => {
    if (teams.length !== 2) {
      alert("Simülasyon için tam 2 takım olmalı.");
      return;
    }
    const team1Skill = teams[0].reduce((sum, player) => sum + player.skill, 0);
    const team2Skill = teams[1].reduce((sum, player) => sum + player.skill, 0);

    // Basit skor tahmini: skill toplamlarına göre rastgele gol üret
    const goalsTeam1 = Math.floor(Math.random() * (team1Skill / 5 + 1));
    const goalsTeam2 = Math.floor(Math.random() * (team2Skill / 5 + 1));

    setMatchResult({ goalsTeam1, goalsTeam2 });
  };

  // Futbol sahası düzeni için pozisyonlara göre CSS sınıfı atanabilir
  // Basit grid yapısı ile gösterim:
  const positionOrder = ["Kaleci", "Defans", "Orta Saha", "Forvet"];

  // Bir takımın oyuncularını pozisyon sırasına göre sırala:
  const sortByPosition = (team) => {
    return positionOrder.flatMap((pos) =>
      team.filter((p) => p.position === pos)
    );
  };

  return (
    <div
      style={{
        backgroundColor: "#0a6b0a",
        minHeight: "100vh",
        color: "white",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "1rem",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h1 style={{ fontSize: "2rem", marginBottom: "1rem", textAlign: "center" }}>
        Rastgele Takım Oluşturucu ve Maç Simülasyonu
      </h1>

      <textarea
        placeholder="Her satıra 'İsim,Seviye(1-10),Pozisyon(Kaleci,Defans,Orta Saha,Forvet)' yazın. Örnek: Ahmet,7,Forvet"
        value={names}
        onChange={(e) => setNames(e.target.value)}
        rows={6}
        style={{
          width: "400px",
          maxWidth: "90vw",
          borderRadius: "8px",
          border: "2px solid #2ecc40",
          padding: "10px",
          fontSize: "1rem",
          marginBottom: "1rem",
          backgroundColor: "#144d14",
          color: "white",
          resize: "vertical",
        }}
      />

      <div style={{ marginBottom: "1rem" }}>
        <button
          onClick={handleGenerate}
          style={{
            backgroundColor: "#2ecc40",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            fontSize: "1rem",
            cursor: "pointer",
            marginRight: "10px",
            color: "#004d00",
            fontWeight: "bold",
            boxShadow: "0 0 8px #2ecc40",
          }}
        >
          Takımları Oluştur
        </button>

        <button
          onClick={simulateMatch}
          disabled={teams.length !== 2}
          style={{
            backgroundColor: teams.length === 2 ? "#f1c40f" : "#aaa",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            fontSize: "1rem",
            cursor: teams.length === 2 ? "pointer" : "not-allowed",
            fontWeight: "bold",
            color: "#663c00",
            boxShadow: teams.length === 2 ? "0 0 8px #f1c40f" : "none",
          }}
        >
          Maçı Simüle Et
        </button>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "4rem",
          flexWrap: "wrap",
          width: "100%",
          maxWidth: "900px",
          marginBottom: "2rem",
        }}
      >
        {teams.map((team, index) => (
          <div
            key={index}
            style={{
              backgroundColor: "#144d14",
              borderRadius: "12px",
              padding: "1rem",
              flex: "1 1 400px",
              boxShadow: "0 0 12px #2ecc40",
            }}
          >
            <h2
              style={{
                textAlign: "center",
                marginBottom: "1rem",
                fontSize: "1.5rem",
                borderBottom: "2px solid #2ecc40",
                paddingBottom: "0.5rem",
              }}
            >
              Takım {index + 1}
            </h2>

            {/* Futbol sahası benzeri düzen */}
            <div
              style={{
                display: "grid",
                gridTemplateRows: "repeat(4, 1fr)",
                rowGap: "0.8rem",
              }}
            >
              {positionOrder.map((pos) => (
                <div
                  key={pos}
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    gap: "0.6rem",
                    flexWrap: "wrap",
                    marginBottom: "0.5rem",
                  }}
                >
                  {sortByPosition(teams[index])
                    .filter((p) => p.position === pos)
                    .map((player, i) => (
                      <div
                        key={i}
                        style={{
                          backgroundColor: "#2ecc40",
                          borderRadius: "50%",
                          width: "60px",
                          height: "60px",
                          display: "flex",
                          flexDirection: "column",
                          justifyContent: "center",
                          alignItems: "center",
                          color: "#004d00",
                          fontWeight: "bold",
                          boxShadow: "0 0 5px #27ae60",
                          fontSize: "0.75rem",
                          textAlign: "center",
                          padding: "4px",
                          userSelect: "none",
                        }}
                        title={`${player.name}\nSkill: ${player.skill}\nPozisyon: ${player.position}`}
                      >
                        <div>{player.name.split(" ")[0]}</div>
                        <div style={{ fontSize: "0.6rem" }}>({player.skill})</div>
                      </div>
                    ))}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {matchResult && (
        <div
          style={{
            backgroundColor: "#2c6e2c",
            padding: "1rem 2rem",
            borderRadius: "10px",
            textAlign: "center",
            boxShadow: "0 0 10px #27ae60",
            maxWidth: "400px",
          }}
        >
          <h2 style={{ fontSize: "1.5rem", marginBottom: "0.5rem" }}>
            Maç Sonucu
          </h2>
          <p style={{ fontSize: "1.2rem" }}>
            Takım 1: {matchResult.goalsTeam1} - Takım 2: {matchResult.goalsTeam2}
          </p>
          <p style={{ fontWeight: "bold", marginTop: "0.5rem" }}>
            {matchResult.goalsTeam1 > matchResult.goalsTeam2
              ? "Takım 1 Kazandı! 🏆"
              : matchResult.goalsTeam1 < matchResult.goalsTeam2
              ? "Takım 2 Kazandı! 🏆"
              : "Berabere! 🤝"}
          </p>
        </div>
      )}

      <footer
        style={{
          marginTop: "auto",
          padding: "1rem",
          color: "#2ecc40",
          fontSize: "0.9rem",
          textAlign: "center",
          width: "100%",
          borderTop: "1px solid #2ecc40",
        }}
      >
        <div style={{ marginBottom: "0.5rem" }}>Sosyal Medya:</div>
        <a
          href="https://twitter.com/"
          target="_blank"
          rel="noreferrer"
          style={{ marginRight: "1rem", color: "#2ecc40" }}
        >
          Twitter
        </a>
        <a
          href="https://facebook.com/"
          target="_blank"
          rel="noreferrer"
          style={{ marginRight: "1rem", color: "#2ecc40" }}
        >
          Facebook
        </a>
        <a
          href="https://instagram.com/"
          target="_blank"
          rel="noreferrer"
          style={{ color: "#2ecc40" }}
        >
          Instagram
        </a>
      </footer>
    </div>
  );
}

export default RandomTeamMaker;
