import React, { useState } from "react";

const positions = ["Kaleci", "Defans", "Orta Saha", "Forvet"];

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teams, setTeams] = useState([]);
  const [matchResult, setMatchResult] = useState(null);
  const [teamNames, setTeamNames] = useState(["TakÄ±m 1", "TakÄ±m 2"]);

  const parsePlayers = () => {
    return names
      .split("\n")
      .map((line) => {
        const [name, skillStr, pos] = line.split(",").map((x) => x.trim());
        return {
          name,
          skill: skillStr ? Number(skillStr) : Math.floor(Math.random() * 10) + 1,
          position: positions.includes(pos) ? pos : positions[Math.floor(Math.random() * positions.length)],
        };
      })
      .filter((player) => player.name !== "");
  };

  const handleGenerate = () => {
    const allPlayers = parsePlayers();
    const result = [[], []];
    allPlayers.forEach((player, index) => {
      result[index % 2].push(player);
    });
    setTeams(result);
    setMatchResult(null);
  };

  const simulateMatch = () => {
    if (teams.length !== 2) {
      alert("SimÃ¼lasyon iÃ§in tam 2 takÄ±m olmalÄ±.");
      return;
    }

    const team1Skill = teams[0].reduce((sum, p) => sum + p.skill, 0);
    const team2Skill = teams[1].reduce((sum, p) => sum + p.skill, 0);

    const goalsTeam1Count = Math.floor(Math.random() * (team1Skill / 5 + 1));
    const goalsTeam2Count = Math.floor(Math.random() * (team2Skill / 5 + 1));

    const generateGoals = (teamIndex, count) => {
      const teamPlayers = teams[teamIndex];
      const goals = [];
      for (let i = 0; i < count; i++) {
        const minute = Math.floor(Math.random() * 90) + 1;
        const scorer = teamPlayers[Math.floor(Math.random() * teamPlayers.length)];
        goals.push({ minute, scorer });
      }
      goals.sort((a, b) => a.minute - b.minute);
      return goals;
    };

    // Kartlar: max 5 tane rastgele (toplam iki takÄ±mda)
    const maxCards = 5;
    const totalPlayers = teams[0].length + teams[1].length;
    const cardCount = Math.floor(Math.random() * (maxCards + 1)); // 0-5 kart arasÄ±

    // Kart tipleri: sarÄ± veya kÄ±rmÄ±zÄ±
    const cardTypes = ["yellow", "red"];

    const generateCards = () => {
      const cards = [];
      for (let i = 0; i < cardCount; i++) {
        const minute = Math.floor(Math.random() * 90) + 1;
        // Hangi takÄ±mdan seÃ§ilecek?
        const teamIndex = Math.random() < 0.5 ? 0 : 1;
        const player = teams[teamIndex][Math.floor(Math.random() * teams[teamIndex].length)];
        const type = cardTypes[Math.floor(Math.random() * cardTypes.length)];
        cards.push({ minute, player, type, teamIndex });
      }
      cards.sort((a, b) => a.minute - b.minute);
      return cards;
    };

    const goalsTeam1 = generateGoals(0, goalsTeam1Count);
    const goalsTeam2 = generateGoals(1, goalsTeam2Count);
    const cards = generateCards();

    setMatchResult({
      goalsTeam1,
      goalsTeam2,
      cards,
      score: [goalsTeam1Count, goalsTeam2Count],
    });
  };

  const resetAll = () => {
    setNames("");
    setTeams([]);
    setMatchResult(null);
    setTeamNames(["TakÄ±m 1", "TakÄ±m 2"]);
  };

  return (
    <div
      style={{
        backgroundColor: "#0a6b0a",
        minHeight: "100vh",
        color: "white",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "1rem",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h1 style={{ fontSize: "2rem", marginBottom: "1rem", textAlign: "center" }}>
        Rastgele TakÄ±m OluÅŸturucu ve MaÃ§ SimÃ¼lasyonu
      </h1>

      <textarea
        placeholder="Her satÄ±ra 'Ä°sim,Seviye(1-10),Pozisyon(Kaleci,Defans,Orta Saha,Forvet)' yazÄ±n. Ã–rnek: Ahmet,7,Forvet"
        value={names}
        onChange={(e) => setNames(e.target.value)}
        rows={6}
        style={{
          width: "400px",
          maxWidth: "90vw",
          borderRadius: "8px",
          border: "2px solid #2ecc40",
          padding: "10px",
          fontSize: "1rem",
          marginBottom: "1rem",
          backgroundColor: "#144d14",
          color: "white",
          resize: "vertical",
        }}
      />

      <div style={{ display: "flex", gap: "1rem", marginBottom: "1rem", flexWrap: "wrap", justifyContent: "center" }}>
        <input
          type="text"
          value={teamNames[0]}
          onChange={(e) => setTeamNames([e.target.value, teamNames[1]])}
          placeholder="1. TakÄ±m Ä°smi"
          style={{
            padding: "8px",
            borderRadius: "8px",
            border: "2px solid #2ecc40",
            backgroundColor: "#144d14",
            color: "white",
            fontWeight: "bold",
            width: "150px",
          }}
        />
        <input
          type="text"
          value={teamNames[1]}
          onChange={(e) => setTeamNames([teamNames[0], e.target.value])}
          placeholder="2. TakÄ±m Ä°smi"
          style={{
            padding: "8px",
            borderRadius: "8px",
