import React, { useState } from "react";

const positions = ["Kaleci", "Defans", "Orta Saha", "Forvet"];

function RandomTeamMaker() {
  const [playersText, setPlayersText] = useState("");
  const [teamCount, setTeamCount] = useState(2);
  const [teamNames, setTeamNames] = useState(["TakÄ±m 1", "TakÄ±m 2"]);
  const [teams, setTeams] = useState([]);
  const [matchResult, setMatchResult] = useState(null);

  // Helper: eÅŸit daÄŸÄ±tÄ±m ve pozisyona gÃ¶re sÄ±ralama
  const createTeams = () => {
    let players = playersText
      .split("\n")
      .map((line) => line.trim())
      .filter((line) => line !== "")
      .map((line) => {
        // Beklenen format: "Ä°sim,Pozisyon,Seviye"
        const parts = line.split(",");
        return {
          name: parts[0].trim(),
          position: parts[1]?.trim() || "Orta Saha",
          skill: Number(parts[2]) || 1,
        };
      });

    if (players.length < teamCount) {
      alert("Oyuncu sayÄ±sÄ± takÄ±m sayÄ±sÄ±ndan az olamaz!");
      return;
    }

    // Pozisyona gÃ¶re grupla
    let grouped = {};
    positions.forEach((pos) => (grouped[pos] = []));
    players.forEach((p) => {
      if (positions.includes(p.position)) {
        grouped[p.position].push(p);
      } else {
        grouped["Orta Saha"].push(p);
      }
    });

    // Her pozisyondan eÅŸit sayÄ±da takÄ±mlara daÄŸÄ±t
    let result = Array.from({ length: teamCount }, () => []);

    positions.forEach((pos) => {
      const list = grouped[pos];
      const shuffled = [...list].sort(() => 0.5 - Math.random());

      shuffled.forEach((p, i) => {
        result[i % teamCount].push(p);
      });
    });

    setTeams(result);
    setMatchResult(null);
  };

  // MaÃ§ simÃ¼lasyonu
  const simulateMatch = () => {
    if (teams.length !== teamCount) {
      alert("Ã–nce takÄ±mlarÄ± oluÅŸturun.");
      return;
    }
    let score = Array(teamCount).fill(0);
    let goalsTeam1 = [];
    let goalsTeam2 = [];
    let cards = [];

    // Basit random simÃ¼lasyon 90 dakika iÃ§inde olaylar
    for (let minute = 1; minute <= 90; minute++) {
      // Her dakika %5 ihtimal gol
      if (Math.random() < 0.05) {
        const teamIndex = Math.floor(Math.random() * teamCount);
        const team = teams[teamIndex];
        if (team.length === 0) continue;
        const scorer = team[Math.floor(Math.random() * team.length)];
        score[teamIndex]++;
        if (teamIndex === 0) goalsTeam1.push({ minute, scorer });
        else goalsTeam2.push({ minute, scorer });
      }

      // Her dakika %3 ihtimal kart
      if (Math.random() < 0.03) {
        const teamIndex = Math.floor(Math.random() * teamCount);
        const team = teams[teamIndex];
        if (team.length === 0) continue;
        const player = team[Math.floor(Math.random() * team.length)];
        const type = Math.random() < 0.3 ? "red" : "yellow";
        cards.push({ minute, player, type, teamIndex });
      }
    }

    setMatchResult({
      score,
      goalsTeam1,
      goalsTeam2,
      cards,
    });
  };

  const resetAll = () => {
    setPlayersText("");
    setTeams([]);
    setMatchResult(null);
    setTeamNames(Array(teamCount).fill("").map((_, i) => `TakÄ±m ${i + 1}`));
  };

  // TakÄ±m isimleri input gÃ¼ncelleme
  const updateTeamName = (index, val) => {
    const newNames = [...teamNames];
    newNames[index] = val;
    setTeamNames(newNames);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#0b610b",
        color: "white",
        display: "flex",
        flexDirection: "column",
