import React, { useState } from "react";

const positions = ["Kaleci", "Defans", "Orta Saha", "Forvet"];

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teams, setTeams] = useState([]);
  const [matchResult, setMatchResult] = useState(null);
  const [teamNames, setTeamNames] = useState(["Takım 1", "Takım 2"]);

  // İsimler, seviye, pozisyon formatında string -> oyuncu objesi
  const parsePlayers = () => {
    return names
      .split("\n")
      .map((line) => {
        const [name, skillStr, pos] = line.split(",").map((x) => x.trim());
        return {
          name,
          skill: skillStr ? Number(skillStr) : Math.floor(Math.random() * 10) + 1,
          position: positions.includes(pos) ? pos : positions[Math.floor(Math.random() * positions.length)],
        };
      })
      .filter((player) => player.name !== "");
  };

  const handleGenerate = () => {
    const allPlayers = parsePlayers();

    // Eşit sayıda takımlara bölme (sadece 2 takım için)
    const result = [[], []];
    allPlayers.forEach((player, index) => {
      result[index % 2].push(player);
    });

    setTeams(result);
    setMatchResult(null);
  };

  const simulateMatch = () => {
    if (teams.length !== 2) {
      alert("Simülasyon için tam 2 takım olmalı.");
      return;
    }

    const team1Skill = teams[0].reduce((sum, player) => sum + player.skill, 0);
    const team2Skill = teams[1].reduce((sum, player) => sum + player.skill, 0);

    const goalsTeam1 = Math.floor(Math.random() * (team1Skill / 5 + 1));
    const goalsTeam2 = Math.floor(Math.random() * (team2Skill / 5 + 1));

    const generateGoals = (teamIndex, goalsCount) => {
      const teamPlayers = teams[teamIndex];
      const goals = [];
      for (let i = 0; i < goalsCount; i++) {
        const minute = Math.floor(Math.random() * 90) + 1;
        const scorer = teamPlayers[Math.floor(Math.random() * teamPlayers.length)];
        goals.push({ minute, scorer });
      }
      goals.sort((a, b) => a.minute - b.minute);
      return goals;
    };

    const goalsTeam1Arr = generateGoals(0, goalsTeam1);
    const goalsTeam2Arr = generateGoals(1, goalsTeam2);

    setMatchResult({
      goalsTeam1: goalsTeam1Arr,
      goalsTeam2: goalsTeam2Arr,
      score: [goalsTeam1, goalsTeam2],
    });
  };

  const resetAll = () => {
    setNames("");
    setTeams([]);
    setMatchResult(null);
    setTeamNames(["Takım 1", "Takım 2"]);
  };

  return (
    <div
      style={{
        backgroundColor: "#0a6b0a",
        minHeight: "100vh",
        color: "white",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "1rem",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h1 style={{ fontSize: "2rem", marginBottom: "1rem", textAlign: "center" }}>
        Rastgele Takım Oluşturucu ve Maç Simülasyonu
      </h1>

      <textarea
        placeholder="Her satıra 'İsim,Seviye(1-10),Pozisyon(Kaleci,Defans,Orta Saha,Forvet)' yazın. Örnek: Ahmet,7,Forvet"
        value={names}
        onChange={(e) => setNames(e.target.value)}
        rows={6}
        style={{
          width: "400px",
          maxWidth: "90vw",
          borderRadius: "8px",
          border: "2px solid #2ecc40",
          padding: "10px",
          fontSize: "1rem",
          marginBottom: "1rem",
          backgroundColor: "#144d14",
          color: "white",
          resize: "vertical",
        }}
      />

      <div style={{ display: "flex", gap: "1rem", marginBottom: "1rem", flexWrap: "wrap", justifyContent: "center" }}>
        <input
          type="text"
          value={teamNames[0]}
          onChange={(e) => setTeamNames([e.target.value, teamNames[1]])}
          placeholder="1. Takım İsmi"
          style={{
            padding: "8px",
            borderRadius: "8px",
            border: "2px solid #2ecc40",
            backgroundColor: "#144d14",
            color: "white",
            fontWeight: "bold",
            width: "150px",
          }}
        />
        <input
          type="text"
          value={teamNames[1]}
          onChange={(e) => setTeamNames([teamNames[0], e.target.value])}
          placeholder="2. Takım İsmi"
          style={{
            padding: "8px",
            borderRadius: "8px",
            border: "2px solid #2ecc40",
            backgroundColor: "#144d14",
            color: "white",
            fontWeight: "bold",
            width: "150px",
          }}
        />
      </div>

      <div style={{ marginBottom: "1rem" }}>
        <button
          onClick={handleGenerate}
          style={{
            backgroundColor: "#2ecc40",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            fontSize: "1rem",
            cursor: "pointer",
            marginRight: "10px",
            color: "#004d00",
            fontWeight: "bold",
            boxShadow: "0 0 8px #2ecc40",
          }}
        >
          Takımları Oluştur
        </button>

        <button
          onClick={simulateMatch}
          disabled={teams.length !== 2}
          style={{
            backgroundColor: teams.length === 2 ? "#f1c40f" : "#aaa",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            fontSize: "1rem",
            cursor: teams.length === 2 ? "pointer" : "not-allowed",
            fontWeight: "bold",
            color: "#663c00",
            boxShadow: teams.length === 2 ? "0 0 8px #f1c40f" : "none",
            marginRight: "10px",
          }}
        >
          Maçı Simüle Et
        </button>

        <button
          onClick={resetAll}
          style={{
            backgroundColor: "#e74c3c",
            border: "none",
            borderRadius: "8px",
            padding: "10px 20px",
            fontSize: "1rem",
            cursor: "pointer",
            fontWeight: "bold",
            color: "white",
            boxShadow: "0 0 8px #e74c3c",
          }}
        >
          Sıfırla
        </button>
      </div>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "4rem",
          flexWrap: "wrap",
          width: "100%",
          maxWidth: "900px",
          marginBottom: "2rem",
        }}
      >
        {teams.map((team, index) => (
          <div
            key={index}
            style={{
              backgroundColor: "#144d14",
              borderRadius: "12px",
              padding: "1rem",
              flex: "1 1 400px",
              boxShadow: "0 0 12px #2ecc40",
            }}
          >
            <h2
              style={{
                textAlign: "center",
                marginBottom: "1rem",
                fontSize: "1.5rem",
                borderBottom: "2px solid #2ecc40",
                paddingBottom: "0.5rem",
              }}
            >
              {teamNames[index]}
            </h2>

            <div
              style={{
                display: "grid",
                gridTemplateRows: "repeat(4, 1fr)",
                rowGap: "0.8rem",
              }}
            >
              {positions.map((pos) => (
                <div
                  key={pos}
                  style={{
                    display: "flex",
                    justifyContent: "center",
                    gap: "0.6rem",
                    flexWrap: "wrap",
                    marginBottom: "0.5rem",
                  }}
                >
                  {team
                    .filter((p) => p.position === pos)
                    .map((player, i) => (
                      <div
                        key={i}
                        style={{
                          backgroundColor: "#2ecc40",
                          borderRadius: "50%",
                          width: "60px",
                          height: "60px",
                          display: "flex",
                          flexDirection: "column",
                          justifyContent: "center",
                          alignItems: "center",
                          color: "#004d00",
                          fontWeight: "bold",
                          boxShadow: "0 0 5px #27ae60",
                          fontSize: "0.75rem",
                          textAlign: "center",
                          padding: "4px",
                          userSelect: "none",
                        }}
                        title={`${player.name}\nSkill: ${player.skill}\nPozisyon: ${player.position}`}
                      >
                        <div>{player.name.split(" ")[0]}</div>
                        <div style={{ fontSize: "0.6rem" }}>({player.skill})</div>
                      </div>
                    ))}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {matchResult && (
        <div
          style={{
            maxWidth: "600px",
            backgroundColor: "#144d14",
            padding: "1rem",
            borderRadius: "12px",
            boxShadow: "0 0 15px #f1c40f",
          }}
        >
          <h2 style={{ textAlign: "center", marginBottom: "1rem" }}>
            Maç Sonucu: {teamNames[0]} {matchResult.score[0]} - {matchResult.score[1]} {teamNames[1]}
          </h2>

          <div style={{ display: "flex", gap: "2rem", justifyContent: "space-around" }}>
            <div>
              <h3 style={{ textAlign: "center" }}>{teamNames[0]} Goller</h3>
              {matchResult.goalsTeam1.length === 0 ? (
                <p style={{ textAlign: "center" }}>Gol Yok</p>
              ) : (
                <ul>
                  {matchResult.goalsTeam1.map(({ minute, scorer }, i) => (
                    <li key={i}>
                      {minute}. dakikada {scorer.name}
                    </li>
                  ))}
                </ul>
              )}
            </div>

            <div>
              <h3 style={{ textAlign: "center" }}>{teamNames[1]} Goller</h3>
              {matchResult.goalsTeam2.length === 0 ? (
                <p style={{ textAlign: "center" }}>Gol Yok</p>
              ) : (
                <ul>
                  {matchResult.goalsTeam2.map(({ minute, scorer }, i) => (
                    <li key={i}>
                      {minute}. dakikada {scorer.name}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default RandomTeamMaker;
