import React, { useState } from "react";

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teamCount, setTeamCount] = useState(2);
  const [teams, setTeams] = useState([]);

  const handleGenerate = () => {
    const nameArray = names
      .split("\n")
      .map((name) => name.trim())
      .filter((name) => name !== "");
    const shuffled = [...nameArray].sort(() => 0.5 - Math.random());
    const result = Array.from({ length: teamCount }, () => []);
    shuffled.forEach((name, index) => {
      result[index % teamCount].push(name);
    });
    setTeams(result);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        backgroundColor: "#2e7d32", // çim yeşili
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      {/* Ana içerik */}
      <main className="p-6 max-w-xl mx-auto flex-grow">
        <h1 className="text-3xl font-bold mb-6 flex items-center justify-center gap-3">
          <span role="img" aria-label="soccer ball">
            ⚽
          </span>
          Rastgele Futbol Takımı Oluşturucu
        </h1>
        <textarea
          className="w-full mb-4"
          rows={6}
          placeholder="Her satıra bir futbolcu ismi yazın"
          value={names}
          onChange={(e) => setNames(e.target.value)}
          style={{
            padding: "12px",
            borderRadius: "10px",
            border: "3px solid #4caf50", // parlak yeşil kenar
            fontSize: "1rem",
            resize: "vertical",
            fontWeight: "600",
            outlineColor: "#81c784", // fokusda açık yeşil
          }}
        />
        <div className="mb-4 flex items-center gap-3">
          <label>Takım Sayısı:</label>
          <input
            type="number"
            min="1"
            className="rounded px-3 py-1 text-black font-semibold"
            style={{ width: "60px" }}
            value={teamCount}
            onChange={(e) => setTeamCount(Number(e.target.value))}
          />
        </div>
        <button
          onClick={handleGenerate}
          style={{
            backgroundColor: "transparent",
            color: "#a5d6a7", // açık yeşil yazı
            border: "3px solid #4caf50",
            padding: "10px 30px",
            borderRadius: "15px",
            fontWeight: "700",
            fontSize: "1.1rem",
            cursor: "pointer",
            transition: "all 0.3s ease",
            boxShadow: "0 0 5px #4caf50",
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = "#4caf50";
            e.currentTarget.style.color = "white";
            e.currentTarget.style.boxShadow = "0 0 12px #388e3c";
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = "transparent";
            e.currentTarget.style.color = "#a5d6a7";
            e.currentTarget.style.boxShadow = "0 0 5px #4caf50";
          }}
        >
          Takımları Oluştur
        </button>

        <div className="mt-8">
          {teams.map((team, index) => (
            <div
              key={index}
              className="mb-5 p-4 rounded shadow-lg"
              style={{
                backgroundColor: "#1b5e20", // koyu yeşil ton
                border: "2px solid #cddc39", // neon yeşil çizgi
              }}
            >
              <h2 className="text-xl font-semibold mb-2 flex items-center gap-2">
                <span role="img" aria-label="jersey">
                  🧥
                </span>
                Takım {index + 1}
              </h2>
              <ul className="list-disc list-inside space-y-1">
                {team.map((name, i) => (
                  <li key={i}>{name}</li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </main>

      {/* Footer - sosyal medya */}
      <footer
        style={{
          backgroundColor: "#1b5e20",
          padding: "1rem 0",
          textAlign: "center",
          borderTop: "2px solid #cddc39",
        }}
      >
        <div style={{ fontSize: "1.5rem", marginBottom: "0.5rem" }}>
          <a
            href="https://twitter.com"
            target="_blank"
            rel="noopener noreferrer"
            style={{ margin: "0 15px", color: "white", textDecoration: "none" }}
            aria-label="Twitter"
          >
            🐦
          </a>
          <a
            href="https://instagram.com"
            target="_blank"
            rel="noopener noreferrer"
            style={{ margin: "0 15px", color: "white", textDecoration: "none" }}
            aria-label="Instagram"
          >
            📸
          </a>
          <a
            href="https://facebook.com"
            target="_blank"
            rel="noopener noreferrer"
            style={{ margin: "0 15px", color: "white", textDecoration: "none" }}
            aria-label="Facebook"
          >
            📘
          </a>
        </div>
        <div style={{ fontSize: "0.9rem", opacity: 0.7 }}>
          © 2025 Futbol Takım Oluşturucu
        </div>
      </footer>
    </div>
  );
}

export default RandomTeamMaker;
