import React, { useState } from "react";

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teamCount, setTeamCount] = useState(2);
  const [teams, setTeams] = useState([]);

  const handleGenerate = () => {
    const nameArray = names
      .split("\n")
      .map((name) => name.trim())
      .filter((name) => name !== "");
    const shuffled = [...nameArray].sort(() => 0.5 - Math.random());
    const result = Array.from({ length: teamCount }, () => []);
    shuffled.forEach((name, index) => {
      result[index % teamCount].push(name);
    });
    setTeams(result);
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-xl font-bold mb-4">Rastgele Takım Oluşturucu</h1>
      <textarea
        className="w-full border p-2 mb-2"
        rows={6}
        placeholder="Her satıra bir isim girin"
        value={names}
        onChange={(e) => setNames(e.target.value)}
      />
      <div className="mb-2">
        <label>Takım Sayısı: </label>
        <input
          type="number"
          min="1"
          className="border px-2 w-16"
          value={teamCount}
          onChange={(e) => setTeamCount(Number(e.target.value))}
        />
      </div>
      <button
        onClick={handleGenerate}
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        Takımları Oluştur
      </button>

      <div className="mt-6">
        {teams.map((team, index) => (
          <div key={index} className="mb-4 border p-2 rounded">
            <h2 className="font-semibold">Takım {index + 1}</h2>
            <ul className="list-disc pl-6">
              {team.map((name, i) => (
                <li key={i}>{name}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default RandomTeamMaker;
