import React, { useState } from "react";

function RandomTeamMaker() {
  const [names, setNames] = useState("");
  const [teamCount, setTeamCount] = useState(2);
  const [teams, setTeams] = useState([]);
  const [matchResult, setMatchResult] = useState(null);

  const handleGenerate = () => {
    const nameArray = names
      .split("\n")
      .map((name) => name.trim())
      .filter((name) => name !== "");
    const shuffled = [...nameArray].sort(() => 0.5 - Math.random());
    const result = Array.from({ length: teamCount }, () => []);
    shuffled.forEach((name, index) => {
      result[index % teamCount].push({ name, skill: Math.floor(Math.random() * 10) + 1 }); // Her oyuncuya 1-10 skill
    });
    setTeams(result);
    setMatchResult(null);
  };

  const simulateMatch = () => {
    if (teams.length !== 2) {
      alert("Simülasyon için tam 2 takım olmalı.");
      return;
    }
    const team1Skill = teams[0].reduce((sum, player) => sum + player.skill, 0);
    const team2Skill = teams[1].reduce((sum, player) => sum + player.skill, 0);

    // Basit skor tahmini: skill toplamlarına göre rastgele gol üret
    const goalsTeam1 = Math.floor(Math.random() * (team1Skill / 5 + 1)); 
    const goalsTeam2 = Math.floor(Math.random() * (team2Skill / 5 + 1));

    setMatchResult({ goalsTeam1, goalsTeam2 });
  };

  return (
    <div className="p-4 max-w-xl mx-auto" style={{ backgroundColor: "#0a6b0a", color: "white", minHeight: "100vh" }}>
      <h1 className="text-2xl font-bold mb-4 text-center">Rastgele Takım Oluşturucu ve Maç Simülasyonu</h1>
      <textarea
        className="w-full border border-green-700 rounded p-2 mb-2"
        rows={6}
        placeholder="Her satıra bir isim girin"
        value={names}
        onChange={(e) => setNames(e.target.value)}
      />
      <div className="mb-2 text-center">
        <label>Takım Sayısı: </label>
        <input
          type="number"
          min="2"
          max="2"
          className="border border-green-700 rounded px-2 w-16"
          value={teamCount}
          onChange={(e) => setTeamCount(Number(e.target.value))}
        />
      </div>
      <div className="flex justify-center gap-4 mb-4">
        <button
          onClick={handleGenerate}
          className="bg-green-700 hover:bg-green-900 text-white px-4 py-2 rounded"
        >
          Takımları Oluştur
        </button>
        <button
          onClick={simulateMatch}
          className="bg-yellow-500 hover:bg-yellow-700 text-white px-4 py-2 rounded"
          disabled={teams.length !== 2}
        >
          Maçı Simüle Et
        </button>
      </div>

      <div>
        {teams.map((team, index) => (
          <div key={index} className="mb-4 border border-green-700 p-2 rounded">
            <h2 className="font-semibold text-lg">Takım {index + 1}</h2>
            <ul className="list-disc pl-6">
              {team.map((player, i) => (
                <li key={i}>{player.name} (Yetenek: {player.skill})</li>
              ))}
            </ul>
          </div>
        ))}
      </div>

      {matchResult && (
        <div className="mt-6 p-4 bg-green-900 rounded text-center">
          <h2 className="text-xl font-bold mb-2">Maç Sonucu</h2>
          <p>
            Takım 1: {matchResult.goalsTeam1} - Takım 2: {matchResult.goalsTeam2}
          </p>
          <p>
            {matchResult.goalsTeam1 > matchResult.goalsTeam2
              ? "Takım 1 Kazandı! 🏆"
              : matchResult.goalsTeam1 < matchResult.goalsTeam2
              ? "Takım 2 Kazandı! 🏆"
              : "Berabere! 🤝"}
          </p>
        </div>
      )}
    </div>
  );
}

export default RandomTeamMaker;
